-- ----------------------------
-- Records of cies_advantage
-- ----------------------------
INSERT INTO `cies_advantage` VALUES ('1', '阿波罗集团背景，5年上市战略规划', '1467993600', '阿波罗集团20年品牌积累，品牌价值100亿。', '10', '0', '1468046928', '', '', '', '', '', '');
INSERT INTO `cies_advantage` VALUES ('2', '业界独创商业模式，领先同行十年', '1467993600', '投资数百万，进行市场调研、行业分析、战略规划、商业模式设计，首创加盟商、合伙人、子公司盈利模型。', '9', '0', '1468046928', '', '', '', '', '', '');
INSERT INTO `cies_advantage` VALUES ('3', '系统稳定性行业第一', '1467993600', '1.稳定的通信协议：支持有线、433、485、wifi、Zigbee协议。2.架构先进：本地主机+Vensi云平台。3.三年行业研发，10万次测试，500天成熟期。\n', '8', '0', '1468046928', '', '', '', '', '', '');
INSERT INTO `cies_advantage` VALUES ('4', '上千家终端门店的运营管理经验', '1467993600', '20年稳扎稳打，Vensi智能拥有1300家终端店面的门店管理、终端销售、团队建设、活动策划、方案定制、安装售后经验。', '7', '0', '1468046928', '', '', '', '', '', '');
INSERT INTO `cies_advantage` VALUES ('5', '智能授权，多用户操作', '1467993600', 'Vensi智能基于对智慧生活的深刻理解，创新推出支持智能授权的智能家居系统，完美解决智慧生活全家参与和操控安全之间的天然矛盾。', '6', '0', '1468046928', '', '', '', '', '', '');
INSERT INTO `cies_advantage` VALUES ('6', '首页定制系统', '1467993600', 'Vensi智慧家庭APP，创新多用户首页定制，完美解决智能系统功能强大与操控繁琐的矛盾。', '5', '0', '1468046928', '', '', '', '', '', '');
INSERT INTO `cies_advantage` VALUES ('7', '场景定制简单快捷', '1467993600', 'Vensi智能家居系统为用户设置了非常强大的智能化功能，随着用户添加新的家居设备、新的家庭成员、季节变化，系统将自动升级换代。Vensi智能操作系统简便快捷，简单明了。', '4', '0', '1468046928', '', '', '', '', '', '');
INSERT INTO `cies_advantage` VALUES ('8', '强大的渠道资源', '1467993600', '20年的品牌积累，Vensi智能拥有大量优质政府、设计公司、商会、协会、装修公司、同学会、物业管理、房地产商、卖场等资源。', '3', '0', '1468046928', '', '', '', '', '', '');
INSERT INTO `cies_advantage` VALUES ('9', '产品齐全 功能丰富 行业第一', '1467993600', 'Vensi智能拥有18万㎡的生产基地，3000㎡的智能演示厅，稳居行业第一。旗下10大系列，40品类，100多种智能产品，全系列覆盖，可以满足终端消费群体的所有需求。', '2', '0', '1468046928', '', '', '', '', '', '');
INSERT INTO `cies_advantage` VALUES ('10', '行业标准制定单位', '1467993600', 'Vensi智能拥有50多项国家专利及认证，自助设计和研发“Vensi云“系统及全系列产品。', '1', '0', '1468046928', '', '', '', '', '', '');

