DROP TABLE IF EXISTS `cies_experience`;
CREATE TABLE `cies_experience` (
  `experience_id` int(11) NOT NULL AUTO_INCREMENT,
  `imageUrl` varchar(255) NOT NULL,
  `title` varchar(255) NOT NULL,
  `showtime` int(255) NOT NULL,
  `content` mediumtext NOT NULL,
  `sort` int(11) NOT NULL DEFAULT '0',
  `showindex` int(2) NOT NULL DEFAULT '0',
  `status` int(2) NOT NULL DEFAULT '1',
  `createtime` int(255) NOT NULL,
  `headtitle` varchar(255) NOT NULL,
  `keywords` varchar(255) NOT NULL,
  `description` varchar(255) NOT NULL,
  `searchtitle` varchar(255) NOT NULL,
  `copyright` varchar(255) NOT NULL,
  `info` mediumtext,
  `author` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`experience_id`)
) ENGINE=MyISAM AUTO_INCREMENT=191 DEFAULT CHARSET=utf8;

