INSERT INTO `cies_policy` VALUES ('1', '品牌支持', '1467993600', '&lt;p&gt;Vensi智能事业合伙人/加盟商拥有公司的正式授权，公司发放授权证书&lt;br /&gt;\nVensi智能事业合伙人拥有公司期权配售权，公司发放授权证书&lt;br /&gt;\nVensi智能事业合伙人拥有公司专属合伙人名片，公司统一定制&lt;/p&gt;\n', '10', '0', '0', '1468046928', '', '', '', '', '', '', '/Public/upload/policy/1/1ca808d5a4187cd32fea96133ba1eea3.jpg');
INSERT INTO `cies_policy` VALUES ('2', '营销支持', '1467993600', '&lt;p&gt;Vensi智能公司统一在官网、微信公众号、新闻媒体上进行广告宣传、推广，线上导流事业合伙人/加盟商进行线下服务成交。&lt;br /&gt;\nVensi智能在官网上传播事业合伙人/加盟商店面信息及联系方式，可让客户直接找到当地事业合伙人/加盟商。&lt;br /&gt;\nVensi智能收集建材、家电、影音、设计、装修等相关行业信息和联系方式，并免费提供给当地事业合伙人/加盟商。&lt;br /&gt;\nVensi智能公司支持事业合伙人/加盟商在当地举办产品推介会或其他形式的推广活动&lt;/p&gt;\n', '9', '0', '0', '1468046928', '', '', '', '', '', '', '');
INSERT INTO `cies_policy` VALUES ('3', '装修支持', '1467993600', '&lt;p&gt;Vensi智能免费为事业合伙人/加盟商提供全国统一的体验中心形象设计、灯箱广告设计、产品展示方案.&lt;br /&gt;\nVensi智能事业合伙人/加盟商按照公司标准进行店面装修，待公司验收达标后，将会获得公司的装修补助&lt;/p&gt;\n', '8', '0', '0', '1468046928', '', '', '', '', '', '', '');
INSERT INTO `cies_policy` VALUES ('4', '技术支持', '1467993600', '&lt;p&gt;Vensi智能事业合伙人/加盟商免费学习使用技术、安装技术、产品知识、技术知识。&lt;br /&gt;\nVensi智能技术人员协助事业合伙人/加盟商上门指导安装样板间。&lt;br /&gt;\nVensi智能不定期的远程指导事业合伙人/加盟商进行软件更新升级。&lt;br /&gt;\nVensi智能长期免费为事业合伙人/加盟商提供技术咨询服务，新产品的功能及安装指导。&lt;br /&gt;\nVensi智能为事业合伙人/加盟商提供工程单的协助安装服务。&lt;/p&gt;\n', '7', '0', '0', '1468046928', '', '', '', '', '', '', '/Public/upload/policy/4/378f59acd945ed03a74c5995a3ae14d9.jpg');
INSERT INTO `cies_policy` VALUES ('5', '招商支持', '1467993600', '&lt;p&gt;Vensi智能指导事业合伙人在当地召开招商会，帮助其在当地招募加盟商&lt;br /&gt;\nVensi智能将收集的意向客户信息全部移交给当地事业合伙人/加盟商&lt;br /&gt;\nVensi智能和事业合伙人/加盟商一起收集相关行业意向客户信息，协助邀请客户参会&lt;br /&gt;\nVensi智能支持事业合伙人召开第一次招商会，并派遣公司高层及招商团队协助&lt;br /&gt;\n签约的事业合伙人/加盟商，合同期内介绍新客户加盟。新加盟客户首批货款的X%奖励给事业合伙人/加盟商&lt;/p&gt;\n', '6', '0', '0', '1468046928', '', '', '', '', '', '', '/Public/upload/policy/5/a3be9da474392373650a4ab717070b2c.jpg');
INSERT INTO `cies_policy` VALUES ('6', '资源支持', '1467993600', '&lt;p&gt;Vensi智能将积累20年的政府资源、设计公司资源、装修公司资源、房地产公司资源商会协会资源、同学会资源、星级酒店别墅资源、物业管理资源等共享给当地的事业合伙人/加盟商。&lt;/p&gt;\n', '5', '0', '0', '1468046928', '', '', '', '', '', '', '');
INSERT INTO `cies_policy` VALUES ('7', '项目支持', '1467993600', '&lt;p&gt;事业合伙人/加盟商的房地产、物联网酒店项目，公司派遣公司高层去现场协助事业合伙人/加盟商谈判&lt;br /&gt;\nVensi智能事业合伙人/加盟商的重要样板客户，公司提供方案设计、制作和技术支持Vensi智能公司对于一些重要工程提供全程跟踪服务，包括方案设计、产品选型、工程施工、系统调试等指导支&lt;/p&gt;\n', '4', '0', '0', '1468046928', '', '', '', '', '', '', '');
INSERT INTO `cies_policy` VALUES ('8', '培训支持', '1467993600', '&lt;p&gt;每月举办一次店长及项目经理的特训营，培养事业合伙人/加盟商销售团队。&lt;br /&gt;\nVensi智能提供全国统一的演示视频。不定期提供宣传资料，形式为电子版资料、宣传彩页、易拉宝、光盘等。&lt;br /&gt;\nVensi商学院提供远程技术支持、售后服务、团队打造，每年定期对事业合伙人\\加盟商的店长、销售人员进行营销及技术培训。&lt;/p&gt;\n', '3', '0', '0', '1468046928', '', '', '', '', '', '', '/Public/upload/policy/8/c581568814dc8c7d7c0ea8260a6bea92.jpg');
INSERT INTO `cies_policy` VALUES ('9', '运营支持', '1467993600', '&lt;p&gt;Vensi智能协助事业合伙人/加盟商在品牌专卖店所在的建材市场(红星美凯龙、居然之家等)挖掘合伙伙伴&lt;br /&gt;\nVensi智能公司统一设计店面销售物料、设备、软装、资料、图册&lt;br /&gt;\nVensi智能指导事业合伙人/加盟商进行业务开拓；指导事业合伙人/加盟商组织员工给房地产和装修公司打电话，发微信，发邮件，邀请体验&lt;br /&gt;\nVensi智能公司协助各地事业合伙人/加盟商对所推荐及邀请的设计师、甲方项目经理来阿波罗公司实地考察，展厅体验、技术交流、项目洽谈，促进项目成交&lt;/p&gt;\n', '2', '0', '0', '1468046928', '', '', '', '', '', '', '/Public/upload/policy/9/ca59d8cc9ee789cce703d65dc28f0bd8.jpg');
INSERT INTO `cies_policy` VALUES ('10', '售后支持', '1467993600', '&lt;p&gt;Vensi智能为事业合伙人/加盟商的终端客户提供技术上门、远程诊断、电话查询等售后服务支持。&lt;/p&gt;\n', '1', '0', '0', '1468046928', '', '', '', '', '', '', '');

-- ----------------------------
-- Table structure for cies_product
-- ----------------------------
DROP TABLE IF EXISTS `cies_product`;
CREATE TABLE `cies_product` (
  `product_id` int(11) NOT NULL AUTO_INCREMENT,
  `product_classify_id` int(11) NOT NULL,
  `imageUrl` varchar(255) NOT NULL,
  `title` varchar(255) NOT NULL,
  `info` varchar(255) NOT NULL,
  `showtime` int(255) NOT NULL,
  `content` mediumtext NOT NULL,
  `sort` int(11) NOT NULL DEFAULT '0',
  `showindex` int(2) NOT NULL DEFAULT '0',
  `status` int(2) NOT NULL DEFAULT '1',
  `createtime` int(255) NOT NULL,
  `lastsavetime` int(255) NOT NULL,
  `product_material_id` varchar(255) DEFAULT NULL,
  `maxUrl` mediumtext,
  `maxUrl1` mediumtext,
  `maxUrl2` mediumtext,
  `link` varchar(255) DEFAULT NULL,
  `type` varchar(255) DEFAULT NULL,
  `power` varchar(255) DEFAULT NULL,
  `recommen` varchar(255) DEFAULT NULL,
  `headtitle` varchar(255) DEFAULT NULL,
  `keywords` varchar(255) DEFAULT NULL,
  `description` varchar(255) DEFAULT NULL,
  `searchtitle` varchar(255) DEFAULT NULL,
  `copyright` varchar(255) DEFAULT NULL,
  `author` varchar(255) DEFAULT NULL,
  `title1` varchar(255) DEFAULT NULL,
  `color` varchar(255) DEFAULT '0',
  `norms` varchar(255) DEFAULT NULL,
  `source` varchar(255) DEFAULT NULL,
  `electricize` varchar(255) DEFAULT NULL,
  `networking` varchar(255) DEFAULT NULL,
  `pid` int(11) DEFAULT NULL,
  PRIMARY KEY (`product_id`)
) ENGINE=MyISAM AUTO_INCREMENT=186 DEFAULT CHARSET=utf8;

