INSERT INTO `cies_product` VALUES ('126', '53', '/Public/upload/product/126/de96c04b9a306d8145a814240616d5c6.png', '背景音乐控制主机', '', '1478016000', '&lt;p&gt;1.四核A9处理器，安卓系统、金属外壳。&lt;br /&gt;\n2.手机控制功能，支持ＤＬＮＡ，ＱPLAY、Airplay等协议。&lt;br /&gt;\n3.7寸高清屏，HDMI高清输入,输出分辨率为1920*1080&lt;br /&gt;\n4.标配通用的网络接口，内置wifi无线连接。&lt;br /&gt;\n5.支持多档定时开机功能，任意设定时间，能设定定时开机的音乐。&lt;br /&gt;\n6.支持多档定时关机功，任意设定时间。&lt;br /&gt;\n7.支持USB鼠标操作，遥控器操作，多点电容触摸屏操作。&lt;br /&gt;\n8.支持USB鼠标操作，遥控器操作，多点电容触摸屏操作。&lt;br /&gt;\n9.支持U盘和TP卡播放功能，最高支持32G。&lt;br /&gt;\n10.内置多种应用软件，天气，音乐，网络收音机，网络电视等。&lt;br /&gt;\n11.音源选择：多路本地音源（内置内存8G+网络FM/网络音乐+内置SD卡口插卡+HOST USB等）、外部音源两路音源&lt;br /&gt;\n12.播放视频：全高清解码H.264、MPEG1/2/4、HD Divx/Xvid、FLV（Flash动画）1080P（1920*1080），RM/RMVB720P，支持码率高达104Mbps。&lt;br /&gt;\n13.收音功能：内置网络收音机接收全球各个国家地区各种语言的网络电台。&lt;br /&gt;\n14.数码录音：内置高清晰麦克风，可设置实现高品质录音，瞬时记录。&lt;br /&gt;\n15.支持屏保显示万年历功能。&lt;br /&gt;\n16.电子相册：支持JPG、BMP、GIF、PNG格式图片.&lt;br /&gt;\n17.预留ZigBee接口。&lt;br /&gt;\n18.固升级:支持固件升级&lt;/p&gt;\n', '0', '0', '1', '1478072635', '1480751104', ',', '/Public/upload/product/126/faa2ccf8d16e177edde14e686f09415c.jpg,/Public/upload/product/126/5ade4440857a4b2b3510d917a91a2b3e.jpg,/Public/upload/product/126/35ed6ef2908002a18e4e9eace832793f.jpg,/Public/upload/product/126/f23f2210833d9a1d1722a9c4e88d40c1.jpg', '', null, '', 'C-6W1001-41 7寸/C-6W1003-41 10寸', '', '', '背景音乐控制主机', '智能家居、Vensi威士丹利、背景音乐控制主机', '', '背景音乐控制主机', '', '', '', '', '123x193x52mm/251x193x57mm', 'AC100-240V  50/60Hz', '', '', '52');
